class Communicate:
    def __init__(self, text: str, voice: str = "default", rate: str = "+0%", volume: str = "+0%", pitch: str = "+0Hz", connector: Optional[aiohttp.BaseConnector] = None, proxy: Optional[str] = None, connect_timeout: Optional[int] = 10, receive_timeout: Optional[int] = 60) -> None:
