        self.state = {
            "partial_text": "",
            "offset": 0,
            "stream_status": "idle",
            "current_chunk_index": 0,
            "drm_token": None
        }

    def _split_text(self, text: str) -> List[str]:
        chunk_size = 1024
        return [text[i:i+chunk_size] for i in range(0, len(text), chunk_size)]

    async def __stream(self) -> AsyncGenerator[TTSChunk, None]:
        try:
            async with aiohttp.ClientSession(timeout=self.session_timeout) as session:
                async with session.ws_connect("wss://tts-service.example.com", proxy=self.proxy, connector=self.connector) as ws:
                    self.state["stream_status"] = "streaming"
                    for chunk in self.texts:
                        await ws.send_str(chunk)
                        while True:
                            msg = await ws.receive()
                            if msg.type == aiohttp.WSMsgType.TEXT:
                                response = msg.data
                                if response.startswith("DRM_CHALLENGE"):
                                    drm_token = self._generate_drm_token()
                                    await ws.send_str(drm_token)
                                    break
                                else:
                                    yield self._parse_message(response)
                            elif msg.type == aiohttp.WSMsgType.CLOSE:
                                await ws.close()
                                return
                            elif msg.type == aiohttp.WSMsgType.ERROR:
                                raise RuntimeError(f"WebSocket error: {ws.exception()}")
                    while True:
                        msg = await ws.receive()
                        if msg.type == aiohttp.WSMsgType.CLOSE:
                            await ws.close()
                            return
                        elif msg.type == aiohttp.WSMsgType.TEXT:
                            yield self._parse_message(msg.data)
                        elif msg.type == aiohttp.WSMsgType.ERROR:
                            raise RuntimeError(f"WebSocket error: {ws.exception()}")
        except Exception as e:
            self.state["stream_status"] = "error"
            raise

    def _generate_drm_token(self) -> str:
        return "drm_token_123"

    def _parse_message(self, message: str) -> TTSChunk:
        import json
        data = json.loads(message)
        return TTSChunk(audio=data["audio"], metadata=data["metadata"])

    async def stream(self) -> AsyncGenerator[TTSChunk, None]:
        try:
            async for chunk in self.__stream():
                yield chunk
        except Exception as e:
            self.state["stream_status"] = "error"
            raise

    def stream_sync(self) -> Generator[TTSChunk, None, None]:
        loop = asyncio.get_event_loop()
        return loop.run_in_executor(None, self._stream_sync_wrapper)

    def _stream_sync_wrapper(self):
        import asyncio
        async def run():
            async for chunk in self.stream():
                yield chunk
        return asyncio.run(run())

