import zmq
import numpy as np
import time
from typing import Optional, Generator, Union, List

class BertClient:
    def __init__(self, ip='localhost', port=5555, port_out=5556, identity=None, timeout=-1):
        self.context = zmq.Context()
        self.sender = self.context.socket(zmq.PUSH)
        self.receiver = self.context.socket(zmq.SUB)
        self.identity = identity or b'client'
        self.request_id = 0
        self.timeout = timeout
        self.pending_request = set()
        self.sender.setsockopt(zmq.SNDTIMEO, self.timeout)
        self.receiver.setsockopt(zmq.RCVTIMEO, self.timeout)
        self.sender.connect(f"tcp://{ip}:{port}")
        self.receiver.connect(f"tcp://{ip}:{port_out}")
        self.receiver.setsockopt(zmq.SUBSCRIBE, b'')

    def close(self):
        self.sender.close()
        self.receiver.close()
        self.context.term()

    def _send(self, msg):
        self.sender.send_pyobj(self.request_id, flags=zmq.SNDMORE)
        self.sender.send_pyobj(msg)

    def _recv(self):
        try:
            return self.receiver.recv_multipart()
        except zmq.Again:
            return None

    def _recv_ndarray(self):
        msg = self._recv()
        if not msg:
            return None
        request_id = msg[0]
        array_data = msg[1]
        if request_id in self.pending_request:
            self.pending_request.remove(request_id)
            return np.frombuffer(array_data, dtype=np.float32)
        return None

    def encode(self, texts, blocking=True):
        self.request_id += 1
        self.pending_request.add(self.request_id)
        self._send(texts)
        if blocking:
            return self._recv_ndarray()
        return None

    def fetch(self, delay=0.0):
        while True:
            response = self._recv_ndarray()
            if response is not None:
                yield response
            if delay > 0:
                time.sleep(delay)
            else:
                break

    def fetch_all(self, sort=True, concat=False):
        responses = []
        while self.pending_request:
            response = self._recv_ndarray()
            if response is not None:
                responses.append(response)
        if sort:
            responses.sort()
        if concat:
            return np.concatenate(responses)
        return responses

