                    msg_parts = self.__msg_socket.recv_multipart()
                    self.__process_message(msg_parts)
            except Exception as e:
                if self.__logging:
                    print(f"Error in recv_handler: {e}")
                self.__terminate = True

    def __process_message(self, msg_parts):
        frame_data = msg_parts[1]
        if self.__compress:
            frame_data = zlib.decompress(frame_data)
        frame = np.frombuffer(frame_data, dtype=np.uint8)
        self.__queue.append(frame)

    def recv(self):
        if self.__queue:
            return self.__queue.popleft()
        return None

    def send(self, frame, message=None):
        if self.__receive_mode:
            raise RuntimeError("Cannot send in receive mode")
        frame_data = frame.tobytes()
        if self.__compress:
            frame_data = zlib.compress(frame_data)
        msg_part = message.encode('utf-8') if message else b''
        self.__msg_socket.send_multipart([msg_part, frame_data])

    def close(self, kill=False):
        self.__terminate = True
        if self.__thread and self.__thread.is_alive():
            self.__thread.join()
        if self.__msg_socket:
            self.__msg_socket.close()
        self.__msg_context.term()

