    async def send_request(self, payload: Dict[str, Any], signature_secret: str, additional_payload_values: Dict[str, Any] = {}, additional_headers: Dict[str, str] = {}, transport: httpx.AsyncHTTPTransport = httpx.AsyncHTTPTransport(retries=2)) -> 'ExternalActionResponse':
        merged_payload = {**payload, **additional_payload_values}
        payload_bytes = json.dumps(merged_payload).encode('utf-8')
        signature = self._encode_payload(payload_bytes, signature_secret)
        headers = {'X-Signature': signature, **additional_headers}
        try:
            async with httpx.AsyncClient(transport=transport) as client:
                response = await client.post(self.url, json=merged_payload, headers=headers)
                response_data = response.json()
                return self._validate_response(response_data)
        except httpx.RequestError as e:
            return ExternalActionResponse(status='error', error=str(e))

    def _encode_payload(self, payload: bytes, signature_secret: str) -> str:
        hmac_signature = hmac.new(signature_secret.encode('utf-8'), payload, hashlib.sha256).digest()
        return base64.b64encode(hmac_signature).decode('utf-8')

    def _validate_response(self, response: Dict[str, Any]) -> 'ExternalActionResponse':
        if response.get('success', False):
            return ExternalActionResponse(status='success', data=response['data'])
        else:
            return ExternalActionResponse(status='error', error=response.get('error'))

