            return (True, response.json())
        return (False, None)

    def is_valid_chain(self, chain: List[dict]) -> bool:
        if len(chain) == 0:
            return True
        if chain[0]['previous_hash'] != 'genesis':
            return False
        for i in range(1, len(chain)):
            current_block = chain[i]
            previous_block = chain[i-1]
            if current_block['previous_hash'] != previous_block['hash']:
                return False
            if not self.is_valid_proof(previous_block['proof'], current_block['hash']):
                return False
        return True

    def is_valid_proof(self, proof: str, block_hash: str) -> bool:
        guess = f"{proof}{block_hash}".encode()
        guess_hash = hashlib.sha256(guess).hexdigest()
        return guess_hash[:4] == "0000"

