import torch
from torchvision import transforms
import io
import base64
import requests

def upload_tensor_to_image_host(image_tensor, api_key, service='smms'):
    if not isinstance(image_tensor, torch.Tensor):
        return None
    to_pil = transforms.ToPILImage()
    pil_image = to_pil(image_tensor)
    buffer = io.BytesIO()
    pil_image.save(buffer, format='PNG')
    base64_str = base64.b64encode(buffer.getvalue()).decode('utf-8')
    if service == 'smms':
        url = 'https://api.smms.app/v2/upload'
        headers = {'Authorization': f'Bearer {api_key}'}
        payload = {'image': base64_str}
        response = requests.post(url, headers=headers, json=payload)
    elif service == 'imgbb':
        url = 'https://api.imgbb.com/1/upload'
        payload = {'key': api_key, 'image': base64_str}
        response = requests.post(url, data=payload)
    else:
        return 'Invalid service specified'
    if response.status_code == 200:
        try:
            data = response.json()
            return data['data']['url']
        except KeyError:
            return 'Error parsing response'
    else:
        return f'Upload failed with status code {response.status_code}'
